import axios from "axios";
import React, { useState, useEffect } from "react";

const App = () => {
  return (
    <div>
      <Country />
    </div>
  );
};

const Country = () => {
  const objInit = {
    name: "Country Name",
  };
  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then((response) => {
      console.log(response.data);
      setCountries(response.data);
    });
  }, []);
  const [countries, setCountries] = useState([]);
  const [newCountry, setNewCountry] = useState("Hello");
  const [searchCountry, setCountry] = useState(objInit);

  const handleOnChange = (event) => {
    const result = Object.keys(countries).filter(
      (e) => e.indexOf(event.target.value) === 0
    );
    console.log(result);
    if (result.length > 0) {
      setCountry(result[0]);
      console.log("sc", searchCountry);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setNewCountry(countries.filter((e) => e.name === newCountry));
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        Find Countries : <input onChange={handleOnChange} />
        <button type="submit">Find Now</button>
      </form>
      <h2>Countries Searched : </h2>
      <hr />
      <DisplayCountry country={searchCountry} />
    </div>
  );
};

const DisplayCountry = ({ country }) => {
  return (
    <li>
      {country.name.common} {country.flag}
    </li>
  );
};

export default App;
