import axios from "axios";
import React, { useState, useEffect } from "react";

const App = () => {
  return (
    <div>
      <Country />
    </div>
  );
};

const Country = () => {
  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then((response) => {
      console.log(response.data);
      setCountries(response.data);
    });
  }, []);
  const [countries, setCountries] = useState([]);
  const [newCountry, setNewCountry] = useState("Hello");
  const [searchCountry, setCountry] = useState([]);

  const handleOnChange = (event) => {
    const result = countries.filter(
      (e) => e.name.common === event.target.value
    );
    setCountry(result[0]);
    console.log("result", result.name.common);
    console.log("searchCountry", searchCountry);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setNewCountry(countries.filter((e) => e.name === newCountry));
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        Find Countries : <input onChange={handleOnChange} />
        <button type="submit">Find Now</button>
      </form>
      <h2>Countries Searched : </h2>
      <hr />
      {/* <DisplayCountry country={searchCountry} /> */}
    </div>
  );
};

const DisplayCountry = ({ country }) => {
  console.log("country", country);
  return <li></li>;
};

export default App;
