import axios from "axios";
import React, { useState, useEffect } from "react";

const App = () => {
  return (
    <div>
      <Country />
    </div>
  );
};

const Country = () => {
  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then((response) => {
      console.log(response.data);
      setCountries(response.data);
    });
  }, []);
  const [countries, setCountries] = useState([]);
  const [newCountry, setNewCountry] = useState("");
  const [searchCountry, setNewSearchCountry] = useState([]);
  const handleOnChange = (event) => {
    setNewCountry(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setNewSearchCountry(countries.filter((e) => e.name === newCountry));
    console.log("country", country);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        Find Countries : <input onChange={handleOnChange} />
        <button type="submit">Find Now</button>
      </form>
      <h2>Countries Searched : </h2>
      <hr />
      {/* <DisplayCountry country={searchCountry} /> */}
    </div>
  );
};

const DisplayCountry = ({ country }) => {
  console.log("country", country);
  return <li></li>;
};

export default App;
