import testUtils from "react-dom/test-utils";
import Collections from "./Collections";

const App = () => {
  return (
    <div>
      {/* <RenderCollections /> */}

      {/* exercise 2.1-2.5 */}
      <TestingReduce />
    </div>
  );
};

const TestingReduce = () => {
  const course = [
    {
      name: "Half Stack application development",
      id: 1,
      parts: [
        {
          name: "Fundamentals of React",
          exercises: 10,
          id: 1,
        },
        {
          name: "Using props to pass data",
          exercises: 7,
          id: 2,
        },
        {
          name: "State of a component",
          exercises: 14,
          id: 3,
        },
        {
          name: "Redux",
          exercises: 11,
          id: 4,
        },
      ],
    },
    {
      name: "Node.js",
      id: 2,
      parts: [
        {
          name: "Routing",
          exercises: 3,
          id: 1,
        },
        {
          name: "Middlewares",
          exercises: 7,
          id: 2,
        },
      ],
    },
  ];

  return (
    <div>
      <CourseDisplay course={course} />
    </div>
  );
};

const CourseDisplay = ({ course }) => {
  const storeTotal = [];
  const storeList = [];
  const handleReducer = () => {
    course.forEach((element) => {
      const myArray = [];
      element.parts.map((c) => myArray.push(c.exercises));
      storeTotal.push(myArray.reduce(reducer));
    });
    return storeTotal.join(" ");
  };

  const reducer = (pv, nv) => pv + nv;

  const handleList = () => {
    let app = document.querySelector("#app");
    course.forEach((element) => {
      element.parts.map((c) => {
        storeList.push(c.name + c.exercises);
      });
    });

    let nodes = storeList.map((lang) => {
      let li = document.createElement("li");
      li.textContent = lang;
      return li;
    });
    app.append(...nodes);
  };

  // const courseName = course.parts[0].name;
  return (
    <div>
      <ul id="app">{handleList()}</ul>

      <p>Total : {handleReducer()}</p>
    </div>
  );
};

const RenderCollections = () => {
  const notes = [
    {
      id: 1,
      content: "HTML is easy",
      date: "2019-05-30T17:30:31.098Z",
      important: true,
    },
    {
      id: 2,
      content: "Browser can execute only JavaScript",
      date: "2019-05-30T18:39:34.091Z",
      important: false,
    },
    {
      id: 3,
      content: "GET and POST are the most important methods of HTTP protocol",
      date: "2019-05-30T19:20:14.298Z",
      important: true,
    },
  ];

  return (
    <div>
      <Collections notes={notes} />
    </div>
  );
};

export default App;
