import axios from "axios";
import React, { useState, useEffect } from "react";

const App = () => {
  return (
    <div>
      <Country />
    </div>
  );
};

const Country = () => {
  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then((response) => {
      console.log(response.data);
      setCountries(response.data);
    });
  }, []);
  const [countries, setCountries] = useState([]);
  const [newCountry, setNewCountry] = useState("Hello");
  const [searchCountry, setSearchCountry] = useState([]);
  const [limiter, setLimiter] = useState(false);
  const [single, setSingle] = useState(false);
  const handleOnChange = (event) => {
    const result = countries.filter(
      (country) =>
        country.name.common
          .toLowerCase()
          .search(event.target.value.toLowerCase()) !== -1
    );
    if (result.length > 0) {
      setSearchCountry(result);
      if (result.length > 10) {
        setLimiter(true);
      } else {
        setLimiter(false);
      }
      if (result.length === 1) {
        setSingle(true);
      } else {
        setSingle(false);
      }
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setNewCountry(countries.filter((e) => e.name === newCountry));
  };

  const handleLimiter = () => {};

  return (
    <div>
      <form onSubmit={handleSubmit}>
        Find Countries : <input onChange={handleOnChange} />
        <button type="submit">Find Now</button>
      </form>
      <h2>Countries Searched : </h2>
      <hr />
      {single ? (
        <SingleCountry country={searchCountry} />
      ) : limiter ? (
        "Too many queries.."
      ) : (
        searchCountry.map((e) => <DisplayCountry key={e.id} country={e} />)
      )}
    </div>
  );
};

const DisplayCountry = ({ country }) => {
  const handleShow = () => {
    return <div>{country.population}</div>;
  };

  return (
    <li>
      {country.name.common} {country.flag} Languages : {country.language}
      <button onClick={() => <div>{country.population}</div>}>show</button>
    </li>
  );
};

const SingleCountry = ({ country }) => {
  const obj = country[0].languages;
  const myArray = [];
  const extractLang = () => {
    for (var key in obj) {
      if (obj.hasOwnProperty(key)) {
        myArray.push(obj[key]);
        console.log("myarray", myArray);
      }
    }
  };
  extractLang();
  console.log();
  return (
    <div>
      <h2>
        {country[0].name.common} {country[0].flag}
      </h2>
      <ul>
        <li>Population : {country[0].population}</li>
      </ul>
      <ul>
        Languages :{" "}
        {myArray.map((e) => (
          <li>{e}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
