import { useState } from "react";

const App = () => {
  const [clicks, setClicks] = useState({
    left: 0,
    right: 0,
  });

  const [allClicks, setAllClicks] = useState([]); //declare array

  const handleLeftClick = () => {
    setAllClicks(allClicks.concat("L"));
    setClicks({
      ...clicks, //previous state remains the same!
      left: clicks.left + 1,
    });
  };

  const handleRightClick = () => {
    setAllClicks(allClicks.concat("R"));
    setClicks({
      ...clicks, //previous state remains the same!
      right: clicks.right + 1,
    });
  };

  console.log(allClicks);
  //   console.log("left : ", clicks.left);
  //   console.log("right : ", clicks.right);
  return (
    <>
      <button onClick={handleLeftClick}>left click</button>
      <button onClick={handleRightClick}>right click</button>
      <p>{allClicks}</p>
    </>
  );
};

export default App;
