import axios from "axios";
import React, { useState, useEffect } from "react";
import { isCompositeComponentWithType } from "react-dom/test-utils";

const App = () => {
  return (
    <div>
      <Country />
    </div>
  );
};

const Country = () => {
  const api_key = process.env.REACT_APP_API_KEY;

  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then((response) => {
      console.log(response.data);
      setCountries(response.data);
    });
  });

  const [countries, setCountries] = useState([]);
  const [newCountry, setNewCountry] = useState("Hello");
  const [searchCountry, setSearchCountry] = useState([]);
  const [limiter, setLimiter] = useState(false);
  const [single, setSingle] = useState(false);
  const handleOnChange = (event) => {
    const result = countries.filter(
      (country) =>
        country.name.common
          .toLowerCase()
          .search(event.target.value.toLowerCase()) !== -1
    );
    if (result.length > 0) {
      setSearchCountry(result);
      if (result.length > 10) {
        setLimiter(true);
      } else {
        setLimiter(false);
      }
      if (result.length === 1) {
        setSingle(true);
      } else {
        setSingle(false);
      }
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setNewCountry(countries.filter((e) => e.name === newCountry));
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        Find Countries : <input onChange={handleOnChange} />
        <button type="submit">Find Now</button>
      </form>
      <h2>Countries Searched : </h2>
      <hr />
      <ul>
        {single ? (
          <SingleCountry country={searchCountry[0]} />
        ) : limiter ? (
          "Too many queries.."
        ) : (
          searchCountry.map((e) => <DisplayCountry key={e.id} country={e} />)
        )}
      </ul>
    </div>
  );
};

const DisplayCountry = ({ country }) => {
  const [toggle, setToggle] = useState(false);

  const handleShow = () => {
    if (toggle) {
      setToggle(false);
    } else {
      setToggle(true);
    }
  };

  return (
    <li>
      {country.name.common} {country.flag} &nbsp;
      <button onClick={() => handleShow()}>
        {toggle ? "Minimize" : "More Info"}
      </button>
      {toggle ? <SingleCountry country={country} /> : ""}
    </li>
  );
};

const SingleCountry = ({ country }) => {
  useEffect(() => {
    axios
      .get(
        `http://api.weatherstack.com/current?access_key=${api_key}
          &query=${country.capital}`
      )
      .then((response) => {
        console.log("weather stack", response.data.current);
      });
  }, [country.capital]);

  const obj = country.languages;
  const myArray = [];
  const extractLang = () => {
    for (var key in obj) {
      if (obj.hasOwnProperty(key)) {
        myArray.push(obj[key]);
        console.log("myarray", myArray);
      }
    }
  };
  extractLang();

  return (
    <div>
      <h2>
        {country.name.common} {country.flag}
      </h2>
      <ul>
        <li>Population : {country.population}</li>
      </ul>
      <ul>
        Languages :{" "}
        {myArray.map((e) => (
          <li>{e}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
