import axios from "axios";
import React, { useState, useEffect } from "react";

const App = () => {
  return (
    <div>
      <Country />
    </div>
  );
};

const Country = () => {
  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then((response) => {
      console.log(response.data);
      setCountries(response.data);
    });
  }, []);
  const [countries, setCountries] = useState([]);
  const [newCountry, setNewCountry] = useState("Hello");
  const [searchCountry, setSearchCountry] = useState([]);
  const [limiter, setLimiter] = useState(false);

  const handleOnChange = (event) => {
    const result = countries.filter(
      (country) =>
        country.name.common
          .toLowerCase()
          .search(event.target.value.toLowerCase()) !== -1
    );
    if (result.length > 0) {
      setSearchCountry(result);
      if (result.length > 10) {
        setLimiter(true);
      } else {
        setLimiter(false);
      }
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setNewCountry(countries.filter((e) => e.name === newCountry));
  };

  const handleLimiter = () => {};

  return (
    <div>
      <form onSubmit={handleSubmit}>
        Find Countries : <input onChange={handleOnChange} />
        <button type="submit">Find Now</button>
      </form>
      <h2>Countries Searched : </h2>
      <hr />
      {limiter
        ? "Too many queries.."
        : searchCountry.map((e) => <DisplayCountry key={e.id} country={e} />)}
    </div>
  );
};

const DisplayCountry = ({ country }) => {
  return (
    <li>
      {country.name.common} {country.flag} Languages : {country.languages[0]}
    </li>
  );
};

export default App;
