import testUtils from "react-dom/test-utils";
import Collections from "./Collections";

const App = () => {
  return (
    <div>
      {/* <RenderCollections /> */}

      {/* exercise 2.1-2.5 */}
      <TestingReduce />
    </div>
  );
};

const TestingReduce = () => {
  const course = [
    {
      name: "Half Stack application development",
      id: 1,
      parts: [
        {
          name: "Fundamentals of React",
          exercises: 10,
          id: 1,
        },
        {
          name: "Using props to pass data",
          exercises: 7,
          id: 2,
        },
        {
          name: "State of a component",
          exercises: 14,
          id: 3,
        },
        {
          name: "Redux",
          exercises: 11,
          id: 4,
        },
      ],
    },
    {
      name: "Node.js",
      id: 2,
      parts: [
        {
          name: "Routing",
          exercises: 3,
          id: 1,
        },
        {
          name: "Middlewares",
          exercises: 7,
          id: 2,
        },
      ],
    },
  ];

  return (
    <div>
      <CourseDisplay course={course} />
    </div>
  );
};

const CourseDisplay = ({ course }) => {
  const item = course[0].parts;
  const array = [];
  const reducer = (pv, nv) => pv + nv;

  const reduceArray = (item, myArray) => {
    item.map((c) => myArray.push(c.exercises));
    console.log("myArray", myArray);
    return myArray.reduce(reducer);
  };

  const handleList = () => {
    let i = 0;
    for (i in course) {
      course[i].parts.map((c) => (
        <li key={c.id}>
          {c.name} {c.exercises}
        </li>
      ));
    }
  };

  const handleReducer = () => {
    let i = 0;
    for (i in course) {
    }

    course.forEach((element) => {
      reduceArray(element.parts, array);
      console.log(element.parts);
    });
  };

  // const courseName = course.parts[0].name;
  return (
    <div>
      <ul id="app">{handleList()}</ul>

      <p>Total : {handleReducer()}</p>
    </div>
  );
};

const RenderCollections = () => {
  const notes = [
    {
      id: 1,
      content: "HTML is easy",
      date: "2019-05-30T17:30:31.098Z",
      important: true,
    },
    {
      id: 2,
      content: "Browser can execute only JavaScript",
      date: "2019-05-30T18:39:34.091Z",
      important: false,
    },
    {
      id: 3,
      content: "GET and POST are the most important methods of HTTP protocol",
      date: "2019-05-30T19:20:14.298Z",
      important: true,
    },
  ];

  return (
    <div>
      <Collections notes={notes} />
    </div>
  );
};

export default App;
