import Collections from "./Collections";

const App = () => {
  return (
    <div>
      {/* <RenderCollections /> */}

      {/* exercise 2.1-2.5 */}
      <TestingReduce />
    </div>
  );
};

const TestingReduce = () => {
  const course = {
    id: 1,
    name: "Half Stack application development",
    parts: [
      {
        name: "Fundamentals of React",
        exercises: 10,
        id: 1,
      },
      {
        name: "Using props to pass data",
        exercises: 7,
        id: 2,
      },
      {
        name: "State of a component",
        exercises: 14,
        id: 3,
      },
    ],
  };

  return (
    <>
      <CourseDisplay course={course} />
    </>
  );
};

const CourseDisplay = ({ course }) => {
  console.log("Course Display Here. ", course.parts[0]);
  const test = course.parts;
  const array1 = [];
  const reducer = (pv, nv) => pv + nv;

  const reduceArray = (myArray) => {
    test.map((c) => myArray.push(c.exercises));
    return myArray.reduce(reducer);
  };

  const handleList = () =>
    test.map((c) => (
      <li key={c.id}>
        {c.name} {c.exercises}
      </li>
    ));

  // const courseName = course.parts[0].name;
  return (
    <div>
      <ul>{handleList()}</ul>

      <p>Redux : {reduceArray(array1)}</p>
    </div>
  );
};

const RenderCollections = () => {
  const notes = [
    {
      id: 1,
      content: "HTML is easy",
      date: "2019-05-30T17:30:31.098Z",
      important: true,
    },
    {
      id: 2,
      content: "Browser can execute only JavaScript",
      date: "2019-05-30T18:39:34.091Z",
      important: false,
    },
    {
      id: 3,
      content: "GET and POST are the most important methods of HTTP protocol",
      date: "2019-05-30T19:20:14.298Z",
      important: true,
    },
  ];

  return (
    <div>
      <Collections notes={notes} />
    </div>
  );
};

export default App;
