import React, { useState } from "react";

const App = () => {
  return (
    <div>
      <Feedback />
    </div>
  );
};

const Feedback = () => {
  const feedback = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  const [userData, setUserData] = useState(feedback);
  console.log(userData);

  const handleGoodEvent = (type) => {
    setUserData({
      ...userData,
      type: userData[type] + 1,
    });
    console.log("Goods : ", userData.good);
  };

  return (
    <>
      <ButtonComponent handleGoodEvent={() => handleGoodEvent(userData.good)} />
    </>
  );
};

const ButtonComponent = (props) => {
  return (
    <div>
      <button onClick={props.handleGoodEvent}>Good </button>

      <button>Neutral </button>
      <button>Bad </button>
    </div>
  );
};

export default App;
