import React, { useState } from "react";

const App = () => {
  return (
    <div>
      <Feedback />
    </div>
  );
};

const Feedback = () => {
  const feedback = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  const [userData, setUserData] = useState(feedback);
  console.log(userData);

  const handleGoodEvent = () => {
    setUserData({
      ...userData,
      good: userData["good"] + 1,
    });
    console.log("Goods : ", userData.good);
  };

  const handleNeutralEvent = () => {
    setUserData({
      ...userData,
      neutral: userData["neutral"] + 1,
    });
    console.log("neutral : ", userData.neutral);
  };

  const handleBadEvent = () => {
    setUserData({
      ...userData,
      bad: userData["bad"] + 1,
    });
    console.log("bad : ", userData.bad);
  };

  return (
    <>
      <ButtonComponent
        handleGoodEvent={() => handleGoodEvent()}
        handleBadEvent={() => handleBadEvent()}
        handleNeutralEvent={() => handleNeutralEvent()}
      />
    </>
  );
};

const ButtonComponent = (props) => {
  console.log(props);
  const { goodEvent, badEvent, neutralEvent } = props;
  return (
    <div>
      <button onClick={goodEvent()}>Good </button>

      <button onClick={props.handleBadEvent}>Bad </button>
      <button onClick={props.handleNeutralEvent}>Neutral </button>
    </div>
  );
};

export default App;
